mapper.select:
SELECT UC.*, UI.UI_NAME, UI.UI_ID FROM USER_CERTI UC
INNER JOIN USER_INFO UI ON UC.UI_NUM = UI.UI_NUM


		AND UC_ACTIVE = #{ucActive}


		AND UC_TYPE = #{ucType}


		AND UI.UI_NUM = #{uiNum}

mapper.update:
UPDATE USER_CERTI


		, UC_TYPE = #{ucType}


		, UC_ACTIVE = #{ucActive} 


		, PERIOD_START = #{periodStart}


		, PERIOD_END = #{periodEnd}


WHERE UC_NUM=#{ucNum}

UPDATE USER_CERTI
SET UC_ACTIVE = #{ucActive}
, PERIOD_START = DATE_FORMAT(NOW(),'%Y%m%d')
, PERIOD_END = DATE_FORMAT(ADDDATE(NOW(),4),'%Y%m%d')
WHERE UC_NUM=#{ucNum}


UPDATE USER_CERTI
SET UC_ACTIVE=0
WHERE UC_NUM=#{ucNum}
