mapper.select:
SELECT * FROM USER_INFO 
WHERE UI_ID=#{uiId}
AND UI_PWD=#{uiPwd}

mapper.update:
UPDATE USER_INFO
SET ACTIVE='1',
UI_PWD = #{uiPwd}
WHERE UI_NUM = #{uiNum}

UPDATE USER_INFO
SET ACTIVE=#{active},
OUTDAT = #{outdat}
WHERE UI_NUM = #{uiNum}

UPDATE USER_INFO
SET ACTIVE='3'	
WHERE UI_ID = #{uiId}

UPDATE USER_INFO
SET ACTIVE=#{active}

	,UI_PWD = UI_ID

WHERE UI_NUM = #{uiNum}
