mapper.select:
SELECT * FROM WORKING_INFO 


		AND WI_NUM = #{wiNum}


		AND UI_NUM = #{uiNum}


		AND WI_DATE = #{wiDate}

SELECT UI.UI_ID, DATE_FORMAT(WI.WI_DATE,'%Y-%m-%d') WI_DATE, DATE_FORMAT(STR_TO_DATE(WI.WI_START,'%H%i%s'),'%T') WI_START, DATE_FORMAT(STR_TO_DATE(WI.WI_END,'%H%i%s'),'%T') WI_END ,WI.WI_ETC FROM WORKING_INFO WI INNER JOIN USER_INFO UI ON UI.UI_NUM = WI.UI_NUM
AND WI.WI_NUM = #{wiNum}
AND WI.UI_NUM = #{uiNum}
AND WI.WI_DATE = #{wiDate}

mapper.update:
UPDATE WORKING_INFO
SET WI_ETC = #{wiEtc}, 
WI_DESC = #{wiDesc}, 
WI_START = #{wiStart}, 
WI_END = #{wiEnd}
WHERE WI_NUM = #{wiNum}
